import React from "react";
import { useState } from "react";
import GapownikCarosuel from "./GapownikCarosuel";

import "./Projects.css";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Projects = () => {
  const [selectedSlide, setSelectedSlide] = useState(null);
  const settings = {
    dots: true,
    infinite: true,
    speed: 700,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
  };

  const handleSelectSlide = (slide) => {
    setSelectedSlide(slide);
  };
  return (
    <section className="projects-container">
      <h5>Projects</h5>
      <div className="info">Click to see more! </div>

      <div className="projects-content">
        {selectedSlide === null ? (
          <Slider {...settings}>
            <div
              className="slide"
              onClick={() => handleSelectSlide("gapownik")}
            >
              <img src="../images/gapownik.png" alt="Project 1" />
              <h3>"Gapownik"</h3>
              {/* <p>
              "Gapownik" is a desktop application that allows users to create
              reports (charts) with Gap values, which are extracted from an
              Excel report generated in the metallographic laboratory. It
              enables users to check how and whether Gap values change over
              days/months/years. It can create charts even from 100 or more
              reports, which would be unattainable for a human in such a short
              time, if at all.
            </p> */}
            </div>
            <div
              className="slide"
              onClick={() => handleSelectSlide("raportowanie")}
            >
              <img src="../images/raportowanie.png" alt="Project 2" />
              <h3>"Raportowanie Wad"</h3>
            </div>
            {/* Dodaj więcej slajdów zgodnie z potrzebą */}
          </Slider>
        ) : // Renderuj odpowiednią karuzelę na podstawie selectedSlide
        // Na przykład:
        selectedSlide === "gapownik" ? (
          <GapownikCarousel />
        ) : (
          <RaportowanieCarousel />
        )}
        )
      </div>
    </section>
  );
};

export default Projects;
